/* ==UserStyle==
@name           ?
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A new userstyle
@author         Me
==/UserStyle== */
@-moz-document domain("news.ycombinator.com") {
	/* Insert code here... */
	:root {
		--c-primary: #0f1226;
		--c-primary-l1: #1d234c;
		--c-primary-l2: #9498af;
		--c-primary-l3: #fd2136;
		--c-secondary: #f7ede2;
		--c-secondary-d1: #999189;
		--c-secondary-l1: #e9ceb0;
		--c-tertiary: #ff6600;
		--m-width: 900px;
		--b-radius: 8px;
		--mono-font: ui-monospace, "Jetbrains Mono", "Fira Code", "Cascadia Code",
		"Menlo", "Monaco", "Cascadia Mono", "Segoe UI Mono", "Roboto Mono",
		"Oxygen Mono", "Ubuntu Monospace", "Source Code Pro", "Fira Mono",
		"Droid Sans Mono", "Courier New", "Consolas", monospace;
	}

	body {
		color: #f7ede2 !important;
	}

	body,
	#hnmain {
		background-color: var(--c-primary) !important;
	}

	textarea {
		font-family: var(--mono-font);
		font-size: 1rem;
		background: var(--c-primary-l1);
		color: var(--c-secondary);
		padding: 10px 10px;
		border: none;
		border-radius: var(--b-radius);
		width: 100% !important;
		max-width: 100% !important;
	}

	input {
		background: var(--c-primary-l1);
		color: var(--c-secondary-l1);
		padding: 5px 10px;
		border: none;
		border-radius: var(--b-radius);
		font-family: var(--mono-font);
	}

	input[type="submit"] {
		font-family: var(--mono-font);
		background: var(--c-primary-l1);
		color: white;
		padding: 10px 20px !important;
		border-radius: var(--b-radius);
		border: none;
		text-transform: uppercase;
		font-size: 0.7rem;
		cursor: pointer;
		transition: background-color 0.1s ease-in-out;
	}

	input[type="submit"]:hover {
		background: var(--c-tertiary);
	}

	.hnname {
		display: inline-block;
		margin-right: 40px;
	}

	#hnmain {
		max-width: var(--m-width) !important;
		margin-left: auto;
		margin-right: auto;
		min-width: 500px !important;
	}

	#hnmain > tbody > tr:nth-child(1) > td {
		background-color: var(--c-primary) !important;
		width: var(--m-width) !important;
	}

	a:link {
		color: var(--c-secondary) !important;
	}

	a:visited {
		color: var(--c-secondary-d1) !important;
	}

	a.morelink {
		padding: 10px 30px;
		display: inline-block;
		background: var(--c-primary-l1);
		border-radius: var(--b-radius);
		transition: background-color 0.1s ease-in-out;
	}

	a.morelink:hover {
		background: var(--c-tertiary);
	}

	a.morelink:visited {
		color: var(--c-secondary) !important;
	}

	#hnmain > tbody > tr:nth-child(1) {
		padding: 12px 0;
		display: flex;
		align-items: center;
		justify-content: center;
		background-color: var(--c-primary) !important;
	}

	#hnmain > tbody > tr:nth-child(3) {
		display: block;
		margin-top: 10px;
	}

	.pageton a,
	.pagetop a:visited {
		color: var(--c-secondary) !important;
	}

	.titleline {
		display: flex;
		justify-content: space-between;
		align-items: center;
		gap: 10px;
		margin-bottom: 10px;
		font-family: var(--mono-font) !important;
		font-size: 1.1rem !important;
	}

	.titleline a {
		transition: color 0.1s ease-in-out;
	}

	.athing:hover .titleline a {
		color: var(--c-tertiary) !important;
	}

	tr.spacer {
		height: 20px !important;
	}

	.subline {
		color: var(--c-primary-l2) !important;
	}

	.subline .hnuser,
	.sitestr {
		color: var(--c-secondary-l1) !important;
	}

	.score {
		color: var(--c-secondary-l1);
	}

	.subtext {
		font-family: Inter !important;
		font-size: 0.8rem !important;
		opacity: 0.4;
		transition: opacity 0.1s ease-in-out;
	}

	.subtext:hover {
		opacity: 1;
	}

	.athing:hover + tr .subtext {
		opacity: 1;
	}

	.votelinks {
		display: inline-block;
		padding: 4px 6px 9px;
		transform: scale(1.1);
		opacity: 0.6;
	}

	.rank {
		font-family: var(--mono-font);
		font-size: 1.2rem;
		color: var(--c-secondary-l1);
		opacity: 0.3;
		font-weight: 800;
	}

	#hnmain > tbody > tr:nth-child(3) > td > div {
		color: var(--c-primary-l2) !important;
	}

	.toptext {
		color: var(--c-secondary);
		font-size: 1rem;
		line-height: 1.3;
		margin: 20px auto;
	}

	.comment-tree .comment .commtext {
		color: var(--c-secondary);
		font-size: 0.9rem;
		line-height: 1.3;
	}

	.comment .commtext {
		color: var(--c-secondary);
		font-size: 0.9rem;
		line-height: 1.3;
	}

	.commtext a:link {
		color: var(--c-secondary-l1) !important;
		transition: color 0.1s ease-in-out;
		text-decoration: none;
	}

	.commtext a:hover {
		color: var(--c-tertiary) !important;
	}

	.commtext pre {
		background: var(--c-primary-l3);
		padding: 15px 20px;
		font-family: var(--mono-font);
		border-radius: var(--b-radius);
		line-height: 1.4;
	}

	.comhead {
		opacity: 0.6;
		font-size: 0.7rem;
		font-family: var(--mono-font);
		flex-shrink: 0;
	}

	.athing:hover .comhead {
		opacity: 1;
	}

	.yclinks a {
		color: var(--c-secondary-l1) !important;
		opacity: 0.4;
		transition: opacity 0.1s ease-in-out;
	}

	.yclinks:hover a {
		opacity: 1;
	}

	.yclinks a:hover {
		color: var(--c-tertiary) !important;
	}

	.pagetop a:hover {
		color: var(--c-tertiary) !important;
	}
}

/* ==UserStyle==
@name           cppReference
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    A new userstyle
@author         Me
==/UserStyle== */
@-moz-document domain("en.cppreference.com") {
	/* Insert code here... */
	.stdrev-select select {
		background-color: #000;
		background-image: linear-gradient(#666, #000);
	}

	/* HACK: : Not sure what could happen next.
 * */
	#carbonads span {
		display: none;
	}

	.t-lines > span {
		color: #ffb86c;
	}
	.t-rev-inl,
	.suggestions-special,
	.t-dcl-h > td,
	.t-dsc-hitem > td,
	div#cpp-head-personal ul,
	.t-dcl-rev > .t-dcl-rev-aux > td,
	fieldset {
		color: #ffb86c;
	}
	code,
	table {
		color: #f67c94;
		background-color: transparent;
	}
	.mw-geshi a {
		color: #afa !important;
	}
	/* ORIG */
	@namespace url(http://www.w3.org/1999/xhtml);
	:root {
		--fg: #eaeaea;
		--bg: #1a1a1a;
		--fg-2: #70c0b1;
		--bg-2: #2a2a2a;
		--bg-odd: #424242;
		--fg-muted: #969896;
		--fg-input: #f0c674;
		--bg-input: #4a4a4a;
		--fg-input-hover: #f0e694;
		--bg-input-hover: #6a6a6a;
		--border: solid 0.5px #373b41;
		--border-color: #373b41;
		--fg-link: #e78c45;
		--fg-link-hover: #e7c547;
		--fg-link-visited: #d54e53;
		--fg-disabled: #ddd;
		--bg-disabled: #777;
		--fg-error: #fff;
		--fg-warning: #000;
		--fg-info: #fff;
		--fg-success: #fff;
		--bg-error: #600;
		--bg-warning: #fa4;
		--bg-info: #14a;
		--bg-success: #151;
		--syntax-fg: #eaeaea;
		--syntax-fg-2: #dadada;
		--syntax-bg: #000000;
		--syntax-bg-2: #2a2a2a;
		--syntax-comment: #969896;
		--syntax-builtin: #c397d8;
		--syntax-function: #7aa6da;
		--syntax-keyword: #ff9da4;
		--syntax-number: #ffc58f;
		--syntax-string: #b9ca4a;
		--syntax-font-family: "Menlo", "Liberation Mono", "Consolas",
		"DejaVu Sans Mono", "Ubuntu Mono", "Courier New", "andale mono",
		"lucida console", monospace;
		--selection-fg: #000;
	}

	/* Selection */
	::-moz-selection {
		color: var(--bg) !important;
		background: var(--fg-link) !important;
		text-shadow: none;
	}
	::selection {
		color: var(--bg) !important;
		background: var(--fg-link) !important;
		text-shadow: none;
	}
	/* Scrollbars */
	::-moz-scrollbar {
		background-color: var(--bg-2) !important;
		width: 10px !important;
	}
	::-moz-scrollbar-track {
		background-color: var(--bg-2) !important;
	}
	::-moz-scrollbar-button {
		display: none !important;
	}
	::-moz-scrollbar-thumb {
		background-color: var(--fg-link) !important;
		border-radius: 4px !important;
		border: none !important;
	}
	::-moz-scrollbar-thumb:hover {
		background-color: var(--fg-link-hover) !important;
	}
	::-webkit-scrollbar {
		background-color: var(--bg-2) !important;
		width: 10px !important;
	}
	::-webkit-scrollbar-track {
		background-color: var(--bg-2) !important;
	}
	::-webkit-scrollbar-button {
		display: none !important;
	}
	::-webkit-scrollbar-thumb {
		background-color: var(--fg-link) !important;
		border-radius: 4px !important;
		border: none !important;
	}
	::-webkit-scrollbar-thumb:hover {
		background-color: var(--fg-link-hover) !important;
	}

	div#cpp-head-second-base,
	div.vectorTabs span,
	div.vectorTabs ul li.selected,
	div.vectorTabs ul,
	div.vectorTabs ul li,
	div.vectorTabs:nth-child(1),
	div#simpleSearch,
	.coliru-btn,
	.coliru-btn:hover,
	.coliru-select-compiler select,
	.ace-tm .ace_indent-guide {
		background-image: none;
	}

	.t-navbar-sep,
	div#simpleSearch button#searchButton img {
		filter: invert(1);
	}

	.mainpagetable tr.row td,
	table.diff {
		background-color: transparent;
	}
	div#cpp-content-base {
		background-color: var(--bg);
	}
	html,
	body,
	div#cpp-head-first-base,
	.mainpagetable tr.rowtop td,
	div#cpp-footer-base,
	table.wikitable > * > tr > th,
	table.mw-cppsearch-groups,
	.t-member,
	.t-inherited,
	#pagehistory li.selected,
	td.diff-context {
		background-color: var(--bg-2);
	}
	div#cpp-head-second-base,
	.t-navbar-menu > div,
	table.fmbox,
	table.fmbox-system,
	table.wikitable,
	.suggestions-special,
	table.ambox,
	div#cpp-head-personal ul,
	td.diff-addedline .diffchange,
	td.diff-deletedline .diffchange {
		background-color: var(--bg-odd);
	}

	div#cpp-head-first-base,
	.mainpagetable tr.rowtop td,
	div#cpp-footer-base,
	table.fmbox,
	table.fmbox-system,
	table.wikitable > * > tr > th,
	table.wikitable,
	div.mw-geshi,
	table.mw-cppsearch-groups,
	.t-member,
	.t-inherited {
		border-color: transparent;
	}
	h1,
	h2,
	h3,
	h4,
	h5,
	.mainpagetable tr.row td,
	.mainpagetable tr.row td:last-child,
	.mainpagetable tr.row td:first-child,
	.t-navbar-menu > div,
	.t-navbar-head,
	table.wikitable > * > tr > td,
	.t-c,
	.t-dsc > td,
	.t-sdsc-begin td,
	.t-rev > td:nth-child(1),
	.t-rev > td:nth-child(2),
	.mw-cppsearch-groups th,
	.mw-cppsearch-groups td,
	table.ambox,
	.t-dcl > td,
	.t-dcl-sep > td,
	.dsctable > tbody > tr:last-child > td,
	.t-inheritance-diagram,
	#userloginForm,
	.t-dcl-rev > .t-dcl:first-child > td,
	.t-dcl-rev > .t-dcl > td,
	.t-cc,
	#pagehistory li,
	td.diff-context {
		border-color: rgba(255, 255, 255, 0.1);
	}
	.t-rev-inl,
	.suggestions-special,
	.t-dcl-h > td,
	.t-dsc-hitem > td,
	div#cpp-head-personal ul,
	.t-dcl-rev > .t-dcl-rev-aux > td,
	fieldset {
		border-color: rgba(255, 255, 255, 0.2);
	}
	#pagehistory li.selected {
		border-color: var(--fg-link);
	}
	hr {
		background-color: rgba(255, 255, 255, 0.1);
	}

	div.vectorTabs li.selected a,
	div.vectorTabs li.selected a:link,
	div.vectorTabs li.selected a:visited,
	div#content,
	table.wikitable,
	td.diff-context,
	.suggestions a.mw-searchSuggest-link {
		color: var(--fg);
	}
	h1,
	h2,
	h3,
	h4,
	h5,
	.t-mark-rev,
	.t-mark-rev.t-mark-ts {
		color: var(--fg-2);
	}
	div#footer ul li,
	.t-mark {
		color: var(--fg-muted);
	}

	a,
	a:link,
	div.vectorTabs ul li a,
	div.vectorTabs li.selected a:hover,
	div#content a.external,
	div#content a.external:link,
	div#content a.extiw,
	div#content a.extiw:link {
		color: var(--fg-link);
	}
	a:hover,
	a:visited:hover,
	div.vectorTabs ul li a:hover,
	div#content a.external:hover,
	div#content a.extiw:hover {
		color: var(--fg-link-hover);
	}
	a:visited,
	div.vectorTabs ul li a:visited,
	div#content a.external:visited,
	div#content a.extiw:visited {
		color: var(--fg-link-visited);
	}

	input,
	textarea,
	div#simpleSearch,
	select,
	.coliru-select-compiler select {
		background-color: var(--bg-input);
	}
	input,
	textarea,
	div#simpleSearch input#searchInput,
	input,
	select,
	.coliru-select-compiler select {
		color: var(--fg-input);
	}
	div#simpleSearch,
	input[type="search"],
	input[type="text"],
	input,
	textarea,
	select,
	.coliru-select-compiler select {
		border-color: rgba(255, 255, 255, 0.2);
	}
	input[type="search"],
	input[type="text"],
	textarea,
	input,
	select,
	.coliru-select-compiler select {
		border-width: 0.5px;
		border-style: solid;
	}

	.coliru-btn,
	input[type="submit"] {
		background-color: var(--fg-link);
		color: var(--bg);
	}
	.coliru-btn:hover,
	input[type="submit"]:hover {
		background-color: var(--fg-link-hover);
		color: var(--bg);
	}
	.coliru-btn {
		border-color: var(--fg-link-hover);
	}
	input[type="submit"] {
		border: solid 0.5px var(--fg-link-hover);
	}

	td[style*="background: #90ff90"] {
		background-color: rgba(30, 255, 30, 0.3) !important;
		color: #fff !important;
	}
	td[style*="background:#ff9090"] {
		background-color: rgba(255, 30, 30, 0.3) !important;
		color: #fff !important;
	}
	td[style*="background: #ececec"] {
		background-color: rgba(80, 80, 80, 0.5) !important;
		color: #fff !important;
	}

	div[style*="background-color: #ffeeaa"] {
		background-color: rgba(150, 100, 0, 0.3) !important;
		border: none !important;
	}
	.errorbox {
		background-color: rgba(255, 30, 30, 0.5);
		color: #fff;
	}

	.mw-plusminus-neg,
	.previewnote,
	div.vectorTabs li.new a {
		color: rgba(255, 30, 30, 0.8);
	}
	.mw-plusminus-pos {
		color: rgba(30, 255, 30, 0.8);
	}

	div.mw-geshi,
	.ace-tm,
	pre {
		background-color: var(--syntax-bg);
	}
	.ace-tm .ace_gutter {
		background-color: var(--syntax-bg-2);
	}
	.ace-tm .ace_gutter,
	.ace-tm .ace_keyword.ace_operator,
	.c.source-c .br0,
	.c.source-c .sy1,
	.c.source-c .sy2,
	.c.source-c .sy3 {
		color: var(--fg-muted);
	}
	.cpp.source-cpp .sy1,
	.cpp.source-cpp .sy4,
	.cpp.source-cpp .br0,
	.cpp.source-cpp .de1,
	.source-text pre,
	div.mw-geshi,
	.ace-tm,
	pre,
	.c.source-c .sy4 {
		color: var(--syntax-fg);
	}
	.cpp.source-cpp .sy2,
	.cpp.source-cpp .sy3 {
		color: var(--syntax-fg-2);
	}
	.cpp.source-cpp .kw2,
	.cpp.source-cpp .kw4,
	.ace-tm .ace_constant,
	.ace-tm .ace_storage,
	.c.source-c .kw4,
	.c.source-c .es1,
	.c.source-c .kw2 {
		color: var(--syntax-builtin);
	}
	.cpp.source-cpp .kw1,
	.cpp.source-cpp .es1,
	.cpp.source-cpp .es2,
	.c.source-c .es5,
	.c.source-c .es2,
	.ace-tm .ace_keyword,
	.c.source-c .kw1 {
		color: var(--syntax-keyword);
	}
	.ace-tm .ace_support.ace_function {
		color: var(--syntax-function);
	}
	.cpp.source-cpp .st0,
	.cpp.source-cpp .st1,
	.cpp.source-cpp .st2,
	.ace-tm .ace_string,
	.ace-tm .ace_constant.ace_language,
	.c.source-c .st0,
	.c.source-c .st1,
	.c.source-c .st2 {
		color: var(--syntax-string);
	}
	.cpp.source-cpp .nu0,
	.cpp.source-cpp .nu1,
	.cpp.source-cpp .nu2,
	.ace-tm .ace_constant.ace_numeric,
	.c.source-c .nu0,
	.c.source-c .nu1,
	.c.source-c .nu2,
	.c.source-c .nu16,
	.c.source-c .nu32,
	.c.source-c .nu64 {
		color: var(--syntax-number);
	}
	.t-spar,
	.cpp.source-cpp .co,
	.cpp.source-cpp .co1,
	.cpp.source-cpp .co2,
	.cpp.source-cpp .coMULTI,
	.c.source-c .co1,
	.c.source-c .co2,
	.c.source-c .co3,
	.ace-tm .ace_comment {
		color: var(--syntax-comment);
	}

	.ace-tm .ace_marker-layer .ace_active-line,
	.ace-tm .ace_gutter-active-line,
	.ace-tm .ace_print-margin {
		background-color: rgba(255, 255, 255, 0.15);
	}
	.ace_cursor {
		border-color: var(--fg-link);
	}
	.ace-tm .ace_cursor {
		color: var(--fg-link);
	}

	.mw-geshi a {
		color: inherit;
		text-decoration: underline;
	}
	.mw-geshi a:hover {
		color: var(--fg-link);
	}
	.mw-geshi a:visited {
		color: var(--fg-muted);
	}

	ul {
		list-style-image: none;
	}

	.t-image {
		filter: invert(1) hue-rotate(180deg);
	}
}

/* ==UserStyle==
@name         Discourse Dark
@version      1.6.1
@description  Darken Discourse forums
@namespace    StylishThemes
@author       StylishThemes
@homepageURL  https://github.com/StylishThemes/Discourse-Dark
@supportURL   https://github.com/StylishThemes/Discourse-Dark/issues
@updateURL    https://raw.githubusercontent.com/StylishThemes/Discourse-Dark/master/discourse-dark.user.css
@license      CC-BY-SA-4.0
@advanced color main-color "Main color" #07c
@advanced dropdown bg-choice "Background" {
  crosshatch "Cross-Hatch" <<<EOT
  url(https://camo.githubusercontent.com/5b8e4cd1a5ce69b6affa17ebd8e4aee00c147618/68747470733a2f2f7374796c6973687468656d65732e6769746875622e696f2f4769744875622d4461726b2f696d616765732f6261636b67726f756e64732f62672d63726f737368617463682e706e67) EOT;
  uniform "Uniform Tile" <<<EOT
  url(https://camo.githubusercontent.com/f83a9a60a2c64be0c88833ccb436e32a458cd2ce/68747470733a2f2f7374796c6973687468656d65732e6769746875622e696f2f4769744875622d4461726b2f696d616765732f6261636b67726f756e64732f62672d74696c652d756e69666f726d2e706e67) EOT;
  random "Random Tile" <<<EOT
